import React, {useState, useEffect} from 'react';
import './App.css';
import GetData from './services/GetData.js';
import Gif from './components/Gif.js'

class App extends React.Components {
  state = {
    gifs: [],
    term: ''
  }

  update = (target) => {
    const response = <GetData keyword={this.state.term} />;

    this.setState({
      term : target
     });
    this.setState({
      gifs: reponse
    })
  };

  render(){
    return (
      <div className="App">
        <div className="container">
          <form>
            <div className="form-group">
              <input
                className="control-form"
                name="termino"
                type="text" placeholder="Busca un gif"
                onChange={e => this.update(e.target)} />
            </div>
          </form>
        </div>

        <Gif gifs={gifs} />

      </div>
    )
  }
}

export default App;
