import React from 'react';
import logo from './logo.svg';
import './App.css';
/*https://api.giphy.com/v1/gifs/search?api_key=DbXQWofnhyYMNBO9AupFJW7CO4V3MBZE&q=panda&limit=100&offset=0&rating=g&lang=en*/
class App extends React.Component {
  state = {
      update: 0,
      gifs: [
          'https://media2.giphy.com/media/1KoN1DMBnCMWk/giphy.webp',
          'https://media2.giphy.com/media/KkqI7oyvsFBm0/giphy.webp',
          'httpss://media3.giphy.com/media/atHI7U3aP3kl2/giphy.webp'
      ]
  }

  componentDidUpdate(prevProps){
      this.setState({update: this.props.update+1});
      console.log("Prev state: ", prevProps);
      console.log("Actual state: ", this.props.update)
  }

  render (){
      return (
        <div className="App">
           <section className="App-content">
              {
                 this.state.gifs.map(singleGif =>
                   <img src={singleGif} />
                 )
              }
           </section >
         </div>
       );
    }
}

export default App;
