import React from 'react';
import './App.css';
import GetData from './services/GetData.js';
import Gif from './components/Gif.js';
import Search from './components/Search.js';

class App extends React.Component {
  state = {
    gifs: [],
    term: '',
    page: 0
  }

  queryApi = () => {
    const term = this.state.term;
    const apiKey = 'DbXQWofnhyYMNBO9AupFJW7CO4V3MBZE';
    const apiURL = `https://api.giphy.com/v1/gifs/search?api_key=${apiKey}&q=${term}&limit=20&offset=0&rating=g&lang=en`;

    fetch(apiURL)
      .then(res => res.json())
      .then(response => {
        const {data = []} = response;

        if(Array.isArray(data)) {
          const gifs = data.map(image =>
            image.images.downsized_medium);
          this.setState({ gifs : gifs })
        }
    });
  }

  searchData = (term) => {
    this.setState({
      term : term
    }, () => {
      this.queryApi();
    })
  };

  render(){
    console.log(this.state)
    return (
      <div className="App">
          <Search searchData={this.searchData} />
          <Gif gifs={this.state.gifs} />
      </div>
    )
  }
}

export default App;
