import React, {useState, useEffect} from 'react';
import './App.css';

const apiURL = `https://api.giphy.com/v1/gifs/search?api_key=DbXQWofnhyYMNBO9AupFJW7CO4V3MBZE&q=minecraft&limit=20&offset=0&rating=g&lang=en`

function App () {
  const [gifs, setGifs] = useState([]);

  useEffect(function () {
    fetch(apiURL)
      .then(res => res.json())
      .then(response => {
        const {data} = response;
        if(Array.isArray(data)) {
          const gifs = data.map(image => image.images.downsized_medium);
          setGifs(gifs);
        }
      });
  }, []);

  return (
    <div className="App">
      <section className="App-content">
        {
            gifs.map(singleGif => <img src={singleGif.url} alt={singleGif.title} />)
        }
      </section>
    </div>
  )
}

export default App;
