const apikey = 'DbXQWofnhyYMNBO9AupFJW7CO4V3MBZE';

function getGifs({keyword = ''}){
    const apiURL =
    `https://api.giphy.com/v1/gifs/search?api_key=${apikey}}
    &q=${keyword}&limit=5&offset=0&rating=g&lang=en`;

    return fetch(apiURL)
      .then(res => res.json())
      .then(response => {
          const {data = []} = response;
          const gifs = data.map(image => {
              const {images, title, id} = image;
              const { url } = images.downsized_medium;
              console.log("hola",{title, id, url});
              return {title, id, url}
          });
          return gifs
      })
}

export default getGifs;
