import React from 'react';
import logo from './logo.svg';
import './App.css';

const url = 'https://api.giphy.com/v1/gifs/search?api_key=DbXQWofnhyYMNBO9AupFJW7CO4V3MBZE&q=panda&limit=2&offset=0&rating=g&lang=en';

class App extends React.Component {
  state = {
      update: 0,
      gifs: []
  }

  componentDidMount(){
      console.log("actualizando...");
      fetch(url)
        .then(res => res.json())
        .then(response => {
            const {data} = response;
            const gifs = data.map(image => image.images.downsized_medium.url)
            this.setState({gifs: gifs})
        })
  }

  render (){
      return (
        <div className="App">
           <section className="App-content">
              {
                 this.state.gifs.map(singleGif =>
                   <img src={singleGif} />
                 )
               }
           </section >
         </div>
       );
    }
}

export default App;
