import React from 'react';
import './App.css';
import { Link, Route } from "wouter";

//Components
import GetData from './services/GetData.js';
import Gif from './components/Gif.js';
import Search from './components/Search.js';


class App extends React.Component {
  state = {
    gifs: [],
    term: '',
    page: 0
  }

  queryApi = () => {
    const term = this.state.term;

    GetData({keyword: term})
      .then(gifs => this.setState({gifs: gifs}))
  }

  searchData = (term) => {
    this.setState({
      term : term
    }, () => {
      this.queryApi();
    })
  };

  render(){
    console.log(this.state)
    return (
      <div className="App">
          <Search searchData={this.searchData} />

          <Link to="/gif/colombia">Colombia</Link>
          <Link to="/gif/argentina">Argentina</Link>
          <Link to="/gif/costarica">Costa rica</Link>
          <Gif gifs={this.state.gifs} />
      </div>
    )
  }
}

export default App;
