import React from 'react';
import logo from './logo.svg';
import './App.css';
import getGifs from './services/getGifs.js';


class App extends React.Component {
  state = {
      gifs: []
  }

  componentDidMount(){
    getGifs({ keyword: 'panda' })
        .then(gifs => this.setState({gifs: gifs}))
  }

  render (){
      return (
        <div className="App">
          <section className="App-content">
            {
              this.state.gifs.map(singleGif =>
                <div>
                  <h5>{singleGif.title}</h5>
                  <small>{singleGif.id}</small>
                  <img src={singleGif.url} />
                </div>
              )
            }
           </section >
        </div>
       );
    }
}

export default App;
